import React,{useState,useEffect,selectChange,cardSelector,Component} from 'react';
import {Button} from '@material-ui/core';
import { Link } from 'react-router-dom';
import Navbar1 from './Navbar1';
import icon11 from './images/icon1.png';
import Grid from '@material-ui/core/Grid';
import { Card, CardMedia, CardContent, CardActions, IconButton,CardHeader,Avatar } from '@material-ui/core';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import './style.css';

const cat='device';

const devices=[
  {category:'device',name:'Sensor1',description:'6.0',status:'Online'},
  {category:'device',name:'Sensor2',description:'6.5',status:'Offline'},
  {category:'device',name:'Sensor3',description:'4.5',status:'Online'},
  {category:'device',name:'Sensor4',description:'4.0',status:'Online'},
  {category:'device',name:'Sensor5',description:'4.5',status:'Online'},
  {category:'device',name:'Sensor6',description:'4.0',status:'Online'},
]


class Home extends Component {
  constructor(){
    super();

    this.state = {
         category:cat
    };
}


  render(){

  return (
    <div>
        <Navbar1/>
        <div className="container4">
        <div className="menu">
            <img  src={icon11}/>
        </div>
        <div className="devices">
          <div className="device1">
            <h3>Devices</h3>
            <Link to='/add_phone_number'  className="link2">
              <Button className="button5" variant="outlined" ><span> +1&nbsp;Add Device</span></Button>
            </Link>
          </div>
          <div className="grid1">
            <Grid container justify="center" spacing={4} className="grid2">
            {devices.filter(device => device.category).map((device, idx) => (
            <div className="device2" key={idx}>
              <Card className="cards1">
                <CardHeader
                avatar={<Avatar className="avatar">pH</Avatar>}
                action={<IconButton aria-label="settings"><MoreVertIcon /></IconButton>}
                title={device.name}/>
                <div className="cardcontent1">
                    <h2>{device.description}pH</h2>
                    <p>{device.status}</p>
                </div>
              </Card>
            </div> ))}
          </Grid></div>
      </div>
        <div className="analytics">
            <p>fegrd</p>
        </div>
        </div>
    </div>
  );
            }
};

export default Home;
